CREATE TABLE `vicenza` (
  `sezione` varchar(100) NOT NULL,
  `testo` varchar(10000) NOT NULL,
  `titolo` varchar(200) NOT NULL,
  `img` varchar(200) NOT NULL,
  `biglietti` varchar(100) DEFAULT NULL,
  `prezzo` float DEFAULT NULL,
  `data_inizio` date DEFAULT NULL,
  `data_fine` date DEFAULT NULL,
  `alt` varchar(500) NOT NULL,
  `id` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

--
-- Dump dei dati per la tabella `vicenza`
--

INSERT INTO `vicenza` (`sezione`, `testo`, `titolo`, `img`, `biglietti`, `prezzo`, `data_inizio`, `data_fine`, `alt`, `id`) VALUES
('eventi', 'Durante la manifestazione funzioner&agrave un ricchissimo stand gastronomico con gnocchi, bigoli, salsicce, braciole, gulasch, polenta, patate fritte, fagioli.. novit&agrave da assaggiare: risotto orzo e baccal&agrave e un strepitoso baccal&agrave alla vicentina.\r\nDa non dimenticare una ricchissima Pesca di Beneficenza (dalle ore 20:00) e l\'apertura straordinaria del &quotPRESTIGIOSO ECOMUSEO DELLA PAGLIA&quot.\r\nE\' disponibile servizio di bus navetta per parcheggio auto presso Istituto S. Antonio. Proporr&agrave sull\'area delle scuole elementari di Agugliaro una settimana di divertimento e aggregazione tra musica, ballo, cabaret e stand gastronomico la sagra di San Bortolo organizzata dalla Pro Loco, al via gioved&iacute alle 21 con la musica di Andrea live e l\'esibizione dei ballerini della scuola di ballo New Harmony Dance di Agugliaro. Venerd&iacute alle 21.15 vi sar&agrave il ritorno del duo Marco & Pippo con l\'esilarante spettacolo di cabaret \'Ist&agrave sul palco\', sabato tributo nazionale alla disco dance con la \'Shary band\', domenica si baller&agrave con \'Michele & Michele by Parioli\', luned&igrave sar&agrave di scena la trascinante afro music del dj Morgan, marted&igrave tocca all\'oorchestra \'Dego\', gioved&igrave gran finale con \'Michele & Michele by Parioli\'. \r\n', 'Una settimana di festa per la sagra di San Bortolo', 'images/vicenza/eventi/sanBortolo.jpg', NULL, 0, '2018-08-16', '2018-08-23', 'Volantino sagra di San Bortolo', 1),
('eventi', 'Vicenza in Festival: il rock balcanico di Goran Bregovic in piazza dei Signori.\r\nLuned&igrave 17 settembre alle 21 in piazza dei Signori a Vicenza si terr&agrave il concerto \'If you don\'t go crazy, you are not normal\' di Goran Bregovic, accompagnato dalla \'Wedding & Funeral Band\' per la rassegna \'Vicenza in Festival 2017\'. Il repertorio spazier&agrave dai suoi grandi successi agli ultimi album (Alkohol e Champagne for Gypsies), con qualche anticipazione di brani del nuovo album in uscita il prossimo anno, accompagnato dalla sua storica formazione di fiati, percussioni e voci bulgare. Con le sue radici natie nei Balcani e la mente nel XXI secolo, le composizioni di Goran Bregovic mescolano le sonorit&agrave di una fanfara tzigana, le polifonie tradizionali bulgare, una chitarra elettrica e percussioni tradizionali con delle accentuazioni rock, dando vita ad una musica dai ritmi travolgenti e dalle sonorit&agrave fragorose, per due ore di pura energia.\r\nGORAN BREGOVIC: nato a Sarajevo da madre serba e padre croato, Goran Bregovic crea i suoi primi gruppi rock a sedici anni. La musical esercit&ograve un ruolo fondamentale nella sua vita come unica possibilit&agrave di poter esprimere pubblicamente il malcontento popolare senza rischiare di finire in galera. Per far piacere ai suoi genitori, Goran si impegna a proseguire i suoi studi di filosofia e sociologia, che lo avrebbero portato ad insegnare, se l\'enorme successo del suo primo disco non avesse deciso altrimenti. Seguono quindici anni con il suo gruppo White Button e tredici album venduti in 6 milioni di copie. Tour interminabili in cui Goran diventer&agrave l\'idolo della giovent&ugrave jugoslava. Alla fine degli anni \'80, Bregovic si libera del suo ruolo sfibrante di \'star\' e si isola in un \'ritiro dorato\' in una piccola casa sulla costa adriatica, un vecchio sogno d\'infanzia. Qui compone le musiche del terzo film di Emir Kusturica \'Il Tempo dei Gitani\'. Ma ben presto i primi disordini scoppiano in Yugoslavia e i due amici sono costretti ad abbandonare tutto e trasferirsi a Parigi. Alla sua origine gi&agrave mista, Goran ha aggiunto una moglie musulmana.<br>\r\n<br>\r\n$BIGLIETTO$\r\n\r\n\r\n', 'Il rock balcanico di Goran Bregovic in piazza dei Signori a Vicenza\r\n', 'images/vicenza/eventi/cantante.png', '<a href=\"php/biglietti.php?id=2&tab=vicenza\">PRENOTA BIGLIETTI</a>', 30, '2018-09-17', '2018-09-17', 'Goran Bregovic circondato dai componenti della sua band durante un concerto', 2),
('eventi', 'C\'&egrave da scoprire la magia del castello, in particolare quella del Castello di Thiene: dal 18 marzo riparte la stagione turistica e questa maestosa Villa Veneta torna ad aprire i suoi cancelli tutti i giorni (tranne il luned&igrave) per il piacere di chi la sceglier&agrave come meta di viaggio (&egrave possibile anche dormire in loco). Una visita al Castello di Thiene, con la guida, &egrave un vero salto indietro nel tempo. \r\n\r\n\"Come si viveva in una dimora del quindicesimo secolo? Quali erano le comodit&agrave di allora (che oggi sarebbero definite come scomodit&agrave)? Qui ci sono sale che testimoniano perfettamente il susseguirsi degli stili di vita secolo dopo secolo\" spiega Francesca di Thiene che con la sua famiglia mantiene la possibilit&agrave di fruire al pubblico questa dimora storica di propriet&agrave privata.\r\n\r\nIl Castello di Thiene &egrave, dal punto di vista storico ed architettonico, uno straordinario esempio di villa prepalladiana; di li a poco, nel 1500, proprio Andrea Palladio comincer&agrave a progettare le dimore arricchite con i colonnati centrali e le due barchesse laterali. Il Castello ha la peculiarit&agrave di associare, in un\'unica costruzione dalla pianta a T, le caratteristiche del castello appunto con quelle del palazzo veneziano ed in particolare il cosiddetto fondaco ossia abitazione e luogo di commercio. Interessante la somiglianza del Castello di Thiene con il Fondaco dei Turchi che si affaccia sul Canal Grande a Venezia.\r\n\r\nDa scoprire i saloni, il grande porticato, le scuderie ma, se siete fortunati e vi va un esperienza fuori dall\'ordinario, potrete provare a richiedere di vistare alcune aree normalmente chiuse al pubblico come le stanze private dove sono conservati oggetti d\'uso quotidiano, cappelli, capi di abbigliamento, ombrelli e accessori; oppure l\'ala est del Castello, anche questa normalmente chiusa, con due sale di rappresentanza realizzate alla fine del\'700, tutta da scoprire.\r\n\r\nRichiedendo invece la visita al parco si pu&ograve accedere a uno dei tre sotterranei realizzati da Francesco Porto, nei primi decenni del XVI secolo per raggiungere pi&ugrave agevolmente le propriet&agrave limitrofe. In questi luoghi si scopre cosa poteva produrre l\'ingegno umano: ad esempio la suggestiva la ghiacciaia, una grande cisterna a forma sferica, che serviva per la raccolta e conservazione del ghiaccio anche durante i mesi estivi. Semplificando era il frigorifero in uso a quei tempi.\r\n\r\n', 'Alla scoperta del Castello di Thiene, il fondaco Veneziano tra le colline del Vicentino', 'images/vicenza/eventi/castelloThiene.jpg', NULL, 0, '2018-03-18', '2018-11-08', 'Facciata del castello di Thiene', 5),
('teatro', 'Saranno ventiquattro gli appuntamenti proposti quest\'anno, per la sua ventitreesima edizione, da Teatro Popolare Veneto, rassegna organizzata dal Comitato vicentino della FITA - Federazione Italiana Teatro Amatori - con il patrocinio di Provincia, RetEventi, Regione del Veneto e FITA Veneto e con la collaborazione di numerose Amministrazioni Comunali.\r\n\r\nRicca e variegata come sempre, la rassegna si prepara dunque a dare il via a questa sua nuova stagione, che inizier&agrave sabato 30 giugno e si concluder&agrave nel gennaio del 2019, coinvolgendo una ventina di compagnie, accomunate dal fatto di proporre, per l\'occasione, un teatro squisitamente popolare, nel senso pi&ugrave alto del termine, e veneto per produzione, autore o ambientazione. Il pubblico potr&agrave gustare un piacevole menu teatrale, formato da grandi classici, capolavori rivisitati, testi originali e lavori musicali, il tutto fra pizzichi di originalit&agrave, affondi nella tradizione e, immancabile, uno spazio pensato per le famiglie e i pi&ugrave piccoli.\r\n', '\"Teatro Popolare Veneto 2018\": spettacoli in tutto il vicentino', 'images/vicenza/teatro/compagniaTeatrale.jpg', NULL, 0, '2018-06-30', '2019-01-26', 'compagnia teatrale sul palco', 6),
('teatro', 'Gioved&igrave 20 settembre alle 21 ci sar&agrave lo spettacolo di danza \"Dreams\" al Teatro Comunale di Thiene in viale Francesco Bassani 18 per la rassegna \"Thiene Danza 2016\". La compagnia \"Kronos\" di Schio con la direzione artistica di Ornella Pegoraro presenta un balletto tra danza classica e moderna in collaborazione con il corso di formazione professionale di Orizzonte Danza ASD Schio.\r\n\r\nLe coreografie saranno realizzate da Barbara Canal, Francesca Foscarini, Eleonora Pasin, Angelo Monaco e Natalia Roig. Saranno ben 17 le interpreti dello spettacolo. Questo lavoro artistico prende ispirazione da \'The Tempest\' di Shakespeare e dalla famosa frase pronunciata da Prospero: \'Noi siamo fatti della stessa sostanza di cui sono fatti i sogni, e nello spazio e nel tempo di un sogno &egrave racchiusa la nostra breve vita\'. Da queste premesse nasce \'Dreams\', una coreografia, che disegna un percorso fra sogno e realt&agrave, creando un varco attraverso il quale possiamo cogliere l\'essenza di noi stessi.<br>\r\n<br>\r\n$BIGLIETTO$\r\n', '\"Dreams\" al Teatro Comunale di Thiene\r\n', 'images/vicenza/teatro/ballerini.jpg', '<a href=\"php/biglietti.php?id=7&tab=vicenza\">PRENOTA BIGLIETTI</a>', 0, '2018-09-20', '2018-09-20', 'ballerine che eseguono uno spettacolo teatrale sul palco', 7),
('teatro', 'Il 6 e 7 Novembre il teatro comunale di Vicenza ospiter&agrave sul proprio palco Angela Finocchiaro.<br>\r\nUna commedia, una danza, un gioco, una festa, questo &egrave HO PERSO IL FILO.\r\n\r\nIn scena un\'Angela Finocchiaro inedita, che si mette alla prova in modo sorprendente con linguaggi espressivi mai affrontati prima, per raccontarci con la sua stralunata comicit&agrave e ironia un\'avventura straordinaria, emozionante e divertente al tempo stesso: quella di un\'eroina pasticciona e anticonvenzionale che parte per un viaggio, si perde, tentenna ma poi combatte fino all\'ultimo il suo spaventoso Minotauro.\r\n\r\nAngela si presenta in scena come un\'attrice stufa dei soliti ruoli: oggi sar&agrave Teseo, il mitico eroe che si infila nei meandri del Labirinto per combattere il terribile Minotauro. Affida agli spettatori un gomitolo enorme da cui dipende la sua vita e parte.\r\n\r\nUna volta entrata nel Labirinto, per&ograve, niente va come previsto. Viene assalita da strane Creature, un misto tra acrobati, danzatori e spiriti dispettosi, che la circondano, la disarmano, la frullano come fosse un frapp&egrave, e soprattutto tagliano il filo che le assicurava la via del ritorno.\r\n\r\nDisorientata, isolata, impaurita, Angela scopre di essere finita in un luogo magico ed eccentrico, un Labirinto, che si esprime con scritte e disegni: ora che ha perso il filo, il Labirinto le lancia un gioco, allegro e crudele per farglielo ritrovare.\r\n\r\nPasso dopo passo, una tappa dopo l\'altra, superando trabocchetti e prove di coraggio, con il pericolo incombente di un Minotauro affamato di carne umana, Angela viene costretta a svelare ansie, paure, ipocrisie che sono sue come del mondo di oggi e a riscoprire il senso di parole come coraggio e altruismo. Alla sua maniera naturalmente, come quando - di fronte ai ragazzi ateniesi che la implorano di salvarli dal Mostro che li sta gi&agrave sgranocchiando - promette firme e impegno sui social; o come quando &egrave sottoposta a una sfida paradossale dal vero Teseo, sceso di corsa dalle vette del mito, indignato perch&egrave la sua interprete difetta delle necessarie qualit&agrave eroiche; o quando deve fare del bene a una mendicante rom e decide di darle non una semplice elemosina ma di regalarle un\'intera spesa: se la porta dietro al supermercato ma, siccome la mendicante la irrita ignorando i prodotti bio per fiondarsi invece su merendine industriali e insaccati carichi di conservanti, finisce per farla arrestare.\r\n\r\n \r\n\r\nLo spettacolo vive del rapporto tra le parole comiche di un personaggio contemporaneo e la fisicit&agrave acrobatica, primitiva, arcaica delle Creature del Labirinto che agiscono, danzano, lottano con Angela provocandola come una gang di ragazzi di strada imprevedibili, spietati e seducenti.\r\n\r\nIl Labirinto &egrave un simbolo antico di nascita - morte - rinascita. Anche Angela, dopo aver toccato il fondo, riuscir&agrave a ritrovare il filo e con esso la forza per affrontare il Minotauro in un finale inatteso che si trasforma in una festa collettiva coinvolgente e liberatoria.<br>\r\n<br>\r\n$BIGLIETTO$', 'Angela Finocchiaro in \'Ho perso il filo\'', 'images/vicenza/teatro/finocchiaro.jpg', '<a href=\"php/biglietti.php?id=7&tab=vicenza\">PRENOTAZIONI DISPONIBILI PER IL GIORNO 07/11/2018</a>', 23.5, '2018-11-06', '2018-11-07', 'Angela Finocchiaro intenta a reggere un filo invisibile come immagine rappresentativa del suo spettacolo', 8),
('eventi', '&ldquo;Cinema sotto le stelle&rdquo; ai Chiostri di Santa Corona: dal 24 giugno al 2 settembre saranno 71 serate al chiaro di luna. Tutte le sere un film diverso comprese le domeniche. Domenica 24 giugno ore 21.30 riparte &ldquo;Cinema sotto le Stelle&rdquo;, la tradizionale rassegna di cinema all&rsquo;aperto ai Chiostri di Santa Corona. Un appuntamento ormai entrato nelle aspettative dei vicentini, che propone una attenta selezione di film di sicuro gradimento, alternando proposte d&rsquo;essai a proiezioni dei migliori film usciti nell\'anno. Quest\'anno, in occasione della mostra in Basilica Palladiana su Basilica palladiana su David Chipperfield, il cinema Odeon, in collaborazione con la Citt&agrave; dell\'Architettura promotrice della mostra stessa, propone la visione di quattro film che portano alla coscienza, dallo sfondo in primo piano, il ruolo attivo dell\'architettura nel cinema e offrono una nuova prospettiva per riflettere sulle relazione tra avvenimenti, uomini e cose. Tutto in un ambiente d&rsquo;eccezione quali i suggestivi e centralissimi Chiostri di Santa Corona, con la possibilit&agrave; di accedere al vicino Cinema Odeon in caso di maltempo e, volendo, servirsi dell\'attiguo parcheggio di via Canove. Le proiezioni di giugno e luglio avranno inizio alle 21.30 e alle 21.15 nei mesi di agosto/settembre. Solo in caso di maltempo in atto, proiezioni e biglietteria saranno al Cinema Odeon. <br>\r\n<br>\r\nECCO ALCUNI FILM IN PROGRAMMA:<br>\r\nDom 24 ASSASSINIO SULL\'ORIENT EXPRESS di Kenneth Branagh<br>\r\nMer 4 UN POSTO TRANQUILLO di John Krasinski <br>\r\nMer 18 THE PLACE di Paolo Genovese <br>\r\nMer 25 IL TUTTOFARE di Valerio Attanasio <br>\r\nDom 29 NICO 1988 di Susanna Nicchiarelli <br>\r\n<br>\r\nBIGLIETTI<br> intero 6 euro, ridotto 5 euro. Abbonamento 5 ingressi 25 euro e abbonamento 10 ingressi &euro; 45 (apertura cassa 30 minuti prima della proiezione). Solo per i possessori di abbonamento 5/10 ingressi ci sar&agrave; una cassa dedicata. Prevendita: segreteria in Corso Palladio 176, Vicenza, dal luned&igrave; al venerd&igrave; dalle 9 alle 12 e dalle 15 alle 17.30.', 'CINEMA SOTTO LE STELLE 2018', 'images/vicenza/eventi/cinema.jpeg', NULL, NULL, '2018-06-24', '2018-09-02', 'Locandina dell\'evento con al centro la scritta &quot;CINEMA SOTTO LE STELLE 2018&quot; con sfondo una scena del fil &quot;assassinio sull\'orient express&quot;', 10),
('teatro', 'Vicenza Danza inizia a Novembre con L\'UNICA DATA ITALIANA DEL TOUR EUROPEO 2018 DEI GRUPO CORPO , compagnia riconosciuta a livello mondiale per la sua originalit&agrave; : un fusion eclettica di danza brasiliana , con i suoi ritmi particolari e le influenze ibride che si fonde con il rigore della danza di ispirazione neoclassica. Creata e guidata dai fratelli Pederneiras , Rodrigo , Paulo e Gabriel , la compagnia presenter&agrave; a Vicenza due brani densi di suggestioni liriche , costruiti sull\'onda della memoria : DANCA SINFONICA brano del 2015 (ideato per celebrare i 40 anni di attivit&agrave;) che esalta l\'originalissimo vocabolario dei movimenti costruiti dal coreografo attraverso la decostruzione della forma,per giungere a nuovi virtuosismi amplificati dalla musica sinfonica che sostiene e avvalla la creazione ; il secondo brano GIRA (percorsi) si nutre di suggestioni etniche di ispirazione religiosa , con musica originale composta ed eseguita appositamente , un percorso poetico ispirato alla rappresentazione dei culti primitivi afro-brasiliani che tocca l\'aspetto universale della spiritualit&agrave; .', 'Dan&ccedil;a Sinfonica - Gira', 'images/vicenza/teatro/danza.jpg', NULL, NULL, '2018-09-26', '2018-09-29', 'Ballerini di danza sinfonica che eseguono una coreografia sul palco', 12),
('cucina', 'IL LOCALE, CONFUSION PUB. Dal 2003 il The Drunken Duck apre (quasi) tutte le sere con lo scopo di proporvi solo ed esclusivamente il pi&ugrave; MEGLIO. Tutto ci&ograve; che offre il nostro bancone &egrave; il frutto di una selezione curatissima, scaturita da una ricerca costante che solo una passione SMODERATA pu&ograve; comprendere. Con cura maniacale testiamo ogni giorno SU DI NOI nuovi prodotti culinari e nuove esperienze brassicole, con l&rsquo;obiettivo di spingere sempre un po pi&ugrave; in l&agrave; l\'asticella della qualit&agrave; che vi viene offerta da oltre 14 anni.<br>\r\nALLA SPINA E IN BOTTIGLIA. Oltre 20 SPINE a rotazione, Botti a caduta, Lambic in box... Pi&ugrave; di 200 ETICHETTE in bottiglia disponibili in mescita, da portare comodamente a casa o da regalare ai meglio amici. Disponibili anche caraffe da asporto, botti e/o fusti per le vostre meglio serate in compagnia! Il locale &egrave; pronto ad accogliere feste ed eventi su prenotazione, anche dedicando su richiesta uno spazio privato al piano superiore. Vi attende uno STAFF PREPARATISSIMO disponibile a guidare le vostre indecisioni, ascoltare le vostre osservazioni o semplicemente eseguire i vostri ordini, garantendovi (quasi) sempre un bel sorriso stampato in faccia.<br>\r\nHAMBURGERIA E CUCINA. Oltre alla meglio selezione brassicola, al TDD potrete gustare una proposta culinaria mai vista. I nostri hamburger sono meticolosamente preparati con i prodotti della &ldquo;Macelleria stellata Damini&rdquo; di Arzignano mentre le nostre verdure provengono dalla societ&agrave; agricola &ldquo;Il Sale della Terra&rdquo; di Quinto Vicentino. Ogni sera i nostri cuochi propongono freschissimi fuori men&ugrave; Vegetariani e Vegani con verdure di stagione. Il nostro men&ugrave; comprende anche favolose bruschette e piatti unici imperdibili. Anche i nostri dolci sono preparati in casa, direttamente dai cuochi della nostra incredibile cucina.\r\n<br><br>\r\nVia Degli Eroi, 77<br>\r\n36050 - Quinto Vincentino - VI<br>\r\nT. 347 471 2738<br>', 'THE DRUNKEN DUCK', 'images/vicenza/cucina/duck.png', NULL, NULL, NULL, NULL, 'Logo del pub Drunken Duck: un\'oca su sfondo nero con contorno rosso con la scritta superiore &quot;drunken duck&quot; e inferiore &quot;le pi&ugrave; meglio birre&quot;', 13),
('cucina', 'A dominare la magnifica Vicenza, citt&agrave; del Palladio, la verde collina di Monte Berico con la famosa Basilica dona un inimitabile respiro alla citt&agrave;. Qui trova la sua realt&agrave; il Ristorante Pizzeria Ai Sette Santi, locale unico nel suo genere, che con le due terrazze panoramiche lascia assaporare, oltre che i suoi piatti, anche tutta la bellezza della citt&agrave; vista dall&rsquo;alto. Dinamismo, grinta e professionalit&agrave; animano i pranzi e le cene del Ristorante Ai Sette Santi, dove in una piacevole atmosfera saranno soddisfatti tutti i gusti e i palati pi&ugrave; esigenti. Fiore all&rsquo;occhiello del Ristorante sono le specialit&agrave; di mare, che vantano una ricerca e una presentazione degna della tipica tradizione della costiera Amalfitana. Le Specialit&agrave; di pesce contraddistinguono il locale: sempre di ottima qualit&agrave;, arriva giornalmente dai vicini mercati di Chioggia, ed &egrave; proposto nel men&ugrave; con ampia variet&agrave;. Da non perdere i must della casa: le crudit&eacute;s di mare , gli ormai famosi Scialatielli all&rsquo;Amalfitana, le cortecce all&rsquo;astice o la classica Frittura di pesce. Per chi ama la carne, il ristorante Ai Sette Santi sapr&agrave; deliziarvi con innumerevoli ricette della tradizione mediterranea, come la fresca tartare di manzo o gli squisiti Scialatielli Delvy, un primo piatto tutto da scoprire. E per i pi&ugrave; golosi un&rsquo;ampia scelta di dessert come il tortino al cioccolato o il tiramis&ugrave; in coppa. La Carta dei vini &egrave; ricca e completa, con le pi&ugrave; rinomate etichette nazionali. E come potrebbe mancare la famosa pizza napoletana? Alla pizzeria Ai Sette Santi &egrave; cotta rigorosamente nei tre forni a legna rotanti, preparata con cura utilizzando ingredienti genuini, delicati e naturalmente gustosi.', 'AI SETTE SANTI RISTORANTE', 'images/vicenza/cucina/7santi.jpg', NULL, NULL, NULL, NULL, 'Facciata arancione del ristorante &quot;Ai sette santi&quot;', 14),
('cucina', 'L\'azienda agricola di oltre 20 ettari si sviluppa nella verde e rilassante pianura di Monteviale a pochi chilometri da Vicenza. L\'agriturismo si trova in una posizione tranquilla facilmente raggiungibilee. La zona &egrave; ricca di percorsi e di sentieri immersi nel verde, come il sentiero della Bada che porta direttamente al paese attraverso un suggestivo percorso nel bosco. L\'azienda si caratterizza per l\'allevamento di bovini da carne, dette sorane, che costituiscono la specialit&agrave; della ristorazione agrituristica. Per fornire un servizio migliore, &egrave; gradita la prenotazione.', 'Ristorante Agriturismo Barco Menti', 'images/vicenza/cucina/barcomenti.jpg', NULL, 0, NULL, NULL, 'Portico del\'agriturismo Barco Menti archi in mattone a vista. Sotto di esso sono presenti dei tavoli con clienti.', 16),
('cucina', 'Osteria tipica vicentina situata nel centro storico della citt&agrave; che propone una cucina casalinga accompagnata da un\'ottima lista di vini. La buona musica e la simpatia del personale assicurano un\'allegra serata. Serate a tema e la possibilit&agrave; di riservare il locale per feste di laurea e altri eventi con musica dal vivo. Locale ben arredato in stile rustico e climatizzato, con oltre 50 coperti. Tra i primi piatti ricordiamo: bigoli all\'anitra, fettuccine funghi freschi, pasta fatta in casa, risotti e tante altre specialit&agrave; di stagione. Secondi piatti: tagliata di manzo, tagliata di petto d\'anitra, straccetti di pollo al curry con riso all\'indiana, filetto di struzzo all\'aceto balsamico, arrosti e specialit&agrave; di stagione. Vasta cantina vini.<br>\r\n<br>\r\nL\'osteria, fino alla met&agrave; del Novecento, era un tipico luogo di ritrovo serale popolare delle persone di sesso maschile; luogo di incontro e di socializzazione ha costituito per lungo tempo, uno dei pochi momenti di incontro e di scambio d\'idee, in aggiunta alla chiesa e alla piazza. Dal dopo guerra ad oggi la frequentazione di questi locali &egrave; venuta sempre meno, negli ultimi anni per&ograve; si &egrave; visto un rifiorire di questi locali che stanno recuperando la loro funzione di luogo di incontro per ambo i sessi.\r\n<br><br>\r\nOrari Aperto dalle 11.30 alle 15 e dalle 18.30 all\'1. <br>\r\nGioved&igrave;, venerd&igrave; e sabato fino alle 2.', 'Osteria i Monelli\r\n\r\n', 'images/vicenza/cucina/monelli.jpg', NULL, NULL, NULL, NULL, 'Interno dell\'osteria ai Monelli con tavoli apparecchiati per il servizio', 17),
('cucina', 'Dalle citt&agrave; di tendenza come Londra, Tokyo e New York, ecco finalmente anche a Vicenza un locale innovativo con un servizio che mancava, visto anche il trend della cultura giapponese che sta diventando un vero e proprio stile di vita da diversi anni anche in europa: ZUSHI.<br>\r\nAll\'interno del locale nella particolarissima &ldquo;ZUSHI-ISLAND&ldquo; troverete le pi&ugrave; squisite specialit&agrave; della cucina giapponese. Molto ricca l\'offerta, con molte composizioni di Sushi, Maki, e Sashimi gi&agrave; pronte in comodi &ldquo;ZUSHI-BOX&rdquo; che possono essere consumate sia all\'intern del locale, o per chi ha fretta, portate via senza perdite di tempo e attese inutili.<br>\r\nProvate ZUSHI anche a mezzogiorno, chiedendo lad esempio a consegna presso il vostro ufficio, per affrontare in modo leggero ed originale una giornata ricca di impegni senza appesantirvi. LAacucina giapponese &egrave; infatti rinomata soprattutto per la sua leggerezza e facilit&agrave; digestiva.<br>\r\nA cena invece potrete iniziare con un aperitivo nell\'elegante &ldquo;ZUSHI-BAR&rdquo; per poi sedervi a degustare uno degli svariati men&ugrave;, godendovi un angolo di giappone moderno e trendy nel cuore di Vicenza.<br>\r\nSERVIZIO A DOMICILIO<br>\r\nChi invece vuole gustarsi le specialit&agrave; della cucina giapponese nella comodit&agrave; di casa propria, pu&ograve; ordinare via telefono o via web le nostre comode vaschette che consegneremo in tempi rapidi in tutta Vicenza citt&agrave;.<br> \r\nZUSHI offre anche un ottimo servizio Catering ed eventi particolari. all\'insegna dele pi&ugrave; squisite specailit&agrave; giapponesi di qualit&agrave;. Per informazioni e prenotazioni contattare direttamente il locale.\r\n', 'Zushi: mangiare sushi a Vicenza\r\n', 'images/vicenza/cucina/zushi.jpg', NULL, NULL, NULL, NULL, 'Interni del ristorante Zushi con tavoli e cucina preparati per il servizio', 18),
('arte', 'Progettato nel 1550 da Andrea Palladio per Girolamo Chiericati, il grandioso edificio &egrave; stato completato alla fine del secolo XVII in forme sostanzialmente fedeli al disegno originario. Il Comune di Vicenza lo acquis&igrave; nel 1839 dalla nobile famiglia dei Chiericati, con l&rsquo;intenzione di raccogliervi le civiche collezioni d&rsquo;arte. Restaurato in quegli anni dagli architetti Berti e Miglioranza, il Museo civico fu inaugurato il 18 agosto del 1855. <br> Al piano terra del palazzo palladiano si possono notare le cinquecentesche decorazioni ad affresco opera di Domenico Brusasorzi (Sala del Firmamento e Sala d&rsquo;Ercole) e Battista Zelotti (Sala del Concilio degli Dei), impreziosite dagli stucchi bianchi e dorati di Bartolomeo Ridolfi e dai motivi a grottesche di Eliodoro Forbicini. Al piano nobile si possono invece ammirare, nella parte cinquecentesca, gli affreschi di Domenico Brusasorzi (Sala delle Virt&ugrave; Civili) e Battista del Moro (Sala della Colonna Traiana), mentre nella parte seicentesca spiccano i soffitti che ospitano, fra una profusione di stucchi bianchi e dorati, alcune tele di Cristoforo Menarola (Apoteosi della famiglia Chiericati e tondi con figure allegoriche) e Bartolomeo Cittadella (Apollo sul Parnaso con la lira e cinque Muse e altre quattro Muse agli angoli). Il piano interrato, riaperto nell&rsquo;ottobre 2012 grazie ad un attento restauro, ha riportato alla luce le fondamenta delle antiche &ldquo;casette Chiericati&rdquo; risalenti al XIV e XV secolo, oltre agli ambienti un tempo dedicati alla servit&ugrave; come le cucine e le cantine, dove ancora oggi si vedono il camino, il pozzo e la suggestiva scala delle botti. Sempre al piano interrato &egrave; inoltre visibile la roggia del Collo, ramo secondario del Bacchiglione coperto con volta a botte risalente al XIII secolo, che delimitava a ovest l&rsquo;area cosiddetta dell&rsquo;Isola, dove sorge Palazzo Chiericati.<br>\r\n<br>\r\n$BIGLIETTO$', 'Palazzo Chiericati', 'images/vicenza/arte/chiericati.jpg', '<a href=\"php/biglietti.php?id=19&tab=vicenza\">PRENOTA BIGLIETTI</a>', 7.5, NULL, NULL, 'Immagine di palazzo chiericati', 19),
('arte', 'Un disegno ideale che differisce dal costruito soprattutto nell&rsquo;ipotesi della cupola centrale (doveva essere pi&ugrave; alta e illuminata da una lanterna), ma che sancisce l&rsquo;idea geometrica di questa architettura: un cerchio, inscritto in un quadrato, le cui divisioni delle sale interne sottostanno a regole proporzionali, a formare il volume cubico che stringe e nasconde il grande vuoto circolare della sala centrale, un dado cui si addossano i cunei che lo ancorano al suolo. Su ogni lato del cubo un frontone di tempio, eliminando cos&igrave; la consueta gerarchia di facciata principale e fronte posteriore; le modeste variazioni delle sale interne, unico elemento di differenziazione assiale, sono testimoniate solo dal diverso numero delle aperture sotto i porticati. I disegni sono indifferenti all&rsquo;orientamento, nella realt&agrave; un Nord-Sud esatto sulla diagonale del cubo, e mostrano i fronti laterali e non quello rivolto verso la citt&agrave;, da cui si arriva. Una indifferenza al contesto che accentua l&rsquo;eccezionale rapporto che solo questa fabbrica stabilisce con il luogo, in geometrica contrapposizione, rispetto al consueto abbraccio delle altre ville palladiane alla campagna veneta.', 'Villa Almerico Capra', 'images/vicenza/arte/rotonda.jpg', NULL, NULL, NULL, NULL, 'Villa Almerico Capra svetta sul giardino circostante', 20),
('arte', 'MUSEO <br>\r\nIl Museo Diocesano Pietro Giacomo Nonis &egrave; tra i pi&ugrave; giovani musei di Vicenza, situato nel &ldquo;cuore sacro&rdquo; della citt&agrave;, la piazza del Duomo, che raccoglie varie e suggestive architetture. Promosso dal Vescovo Pietro G. Nonis e inaugurato dall&rsquo;arcivescovo Cesare Nosiglia, &egrave; nato nel 2005: al suo interno sono alcune delle opere pi&ugrave; preziose e significative del percorso storico, artistico e culturale della Chiesa vicentina. Il Museo Diocesano gestisce anche gli accessi all&rsquo;area Archeologica della Cattedrale.<br>\r\nSERVIZI<br>\r\nIl Museo Diocesano, al fine di rendere fruibile il proprio patrimonio a scopo educativo, culturale, ricreativo, offre alcuni servizi che consentono al visitatore di apprezzare pienamente la visita attraverso appositi strumenti messi a disposizione dell&rsquo;utenza per godere pienamente dell&rsquo;accostamento agli spazi museali.<br><br>EVENTI<br>\r\nIl Museo Diocesano ha tra le proprie finalit&agrave; oltre alla gestione e cura delle collezioni e all&rsquo;offerta di Servizi, anche l&rsquo;ideazione e l&rsquo;accoglienza di eventi volti a promuovere gli obiettivi propri dell&rsquo;Istituzione. Il Museo organizza, ospita e promuove mostre, conferenze, attivit&agrave; e iniziative culturali, didattiche e pastorali.', 'Museo Diocesano', 'images/vicenza/arte/museoDiocesano.jpg', NULL, NULL, NULL, NULL, 'Museo Diocesano ', 21),
('arte', 'Il Museo del Risorgimento e della Resistenza &egrave; intimamente legato alla vita morale, culturale ed alle tradizioni della citt&agrave; e del suo territorio, per una serie di motivi ed argomenti che possono essere spiegati dal materiale conservato e dall\'ubi&shy;cazione stessa della sede museale. L&rsquo;Istituto raccoglie infatti memorie di eventi e di personaggi che appartengono alla storia d\'Italia e che furono pro&shy;tagonisti nelle vicende storiche della citt&agrave;. E ancora, sul colle Ambellicopoli dove sorge l\'edificio di villa Guiccioli, attuale sede del Museo, si svolse l\'eroica resistenza del 1848 che vide la popolazione vicentina, impegnata per la difesa della citt&agrave;, in unione con i volontari provenienti da varie regioni della penisola. Le raccolte conservate dal Museo sono quanto mai varie ed interessanti e anche un breve elenco pu&ograve; dare veramente la misura di questa ricchezza; il nucleo principale della documentazione &egrave; infatti costituito da pubblicazioni a stampa, periodici, giornali, manoscritti, ritratti, quadri, stampe, diari, bandi e proclami, decreti, atti privati, monete, medaglie e decorazioni, carte geografiche civili e militari, armi bianche e da fuoco, bandiere, oggettistica militare di vario genere. Materiali con i quali non &egrave; difficile individuare un filo storico conduttore: i documenti e i cimeli delle raccolte rappresentano infatti un&rsquo;interessante testimonian&shy;za degli avvenimenti vicentini e nazionali&shy; ed in qualche caso europei, delle vicende storiche che vanno dalla prima campagna d\'Italia di Napoleone nel 1796 alla fine della Seconda Guerra Mondiale e alla lotta di liberazione (1945); si tratta di un secolo e mezzo di vicende che hanno trasformato pi&ugrave; volte il volto politico, sociale, economico e morale d&rsquo;Italia e d&rsquo;Europa.', 'Il Museo del Risorgimento e della Resistenza', 'images/vicenza/arte/museoRisorgimento.jpg', NULL, NULL, NULL, NULL, 'Piazza situata davanti all\'ingresso del museo nella quale sono esposti alcuni cannoni e obici austriaci, risalenti alla prima guerra mondiale.', 22),
('arte', 'Palazzo Barbaran da Porto &egrave; un edificio a due piani con soprastante attico.<br> Un imponente atrio a quattro colonne conduce alla rettangolare corte interna, definita sul lato sud e all&rsquo;angolo sud-est da doppio ordine di logge. Il fronte principale su contr&agrave; Porti &egrave; articolato da un&rsquo;intelaiatura di semicolonne ioniche al piano terra bugnato e semicolonne corinzie al piano nobile; la composizione si caratterizza per la posizione non assiale del portale d&rsquo;ingresso, affiancato a sinistra da cinque campate e a destra da tre. Sopra le finestre del piano terra, rettangolari, e attorno a quelle del piano nobile, a edicola con timpani alternati e balconcini con balaustre, si trovano decorazioni in stucco, presenti anche lungo i fregi dei due ordini. Le sale rappresentative del piano terra e del piano nobile presentano ricche decorazioni pittoriche e stucchi. Di particolare rilevanza l&rsquo;atrio a tre navate, sviluppato in profondit&agrave; e costituito dalla successione di tre volte a crociera su colonne ioniche; le volte si estendono sulle due navate laterali, e sono rette sui muri d&rsquo;ambito da semicolonne ioniche che, collegate da trabeazioni alle colonne centrali, compongono una successione di serliane. Con questo espediente viene dissimulata l&rsquo;irregolarit&agrave; planimetrica dell&rsquo;ingresso, attraverso opportune variazioni della lunghezza dei tratti di trabeazione, che consentono di mantenere costanti gli intercolumni centrali. <br><br> Il progetto palladiano del 1570 fu finalizzato alla ricomposizione in forme monumentali di un preesistente palazzo del conte Montano Barbarano; l&rsquo;acquisto dell&rsquo;adiacente abitazione dai cugini Giulio e Alessandro Barbarano comport&ograve; l&rsquo;estensione sbilanciata del fronte principale verso sinistra, verosimilmente effettuata tra il 1571 e il 1574. Il Palladio segu&igrave; direttamente solo i lavori per l&rsquo;erezione della facciata; la sistemazione della corte interna fu probabilmente diretta da un collaboratore, ma la concezione della loggia e della parete di fondo dell&rsquo;atrio &egrave; indubbiamente un&rsquo;interessante invenzione palladiana. Gli stucchi esterni sono dovuti a Lorenzo Rubini e bottega in contemporanea all&rsquo;intervento palladiano, negli anni 1570-72. L&rsquo;apparato decorativo interno &egrave; opera, per quanto riguarda gli stucchi, di Lorenzo Rubini e soprattutto del figlio Agostino. La decorazione pittorica (affreschi e tele), realizzata in concomitanza al riassetto architettonico del palazzo e nel successivo decennio, &egrave; attribuita a vari artisti: Anselmo Canera, Giambattista Zelotti, Gianantonio Fasolo, Giambattista Maganza il Vecchio e ad Andrea Michieli, detto il Vicentino; parte di quelli del piano terra, datati intorno al 1566, risalirebbero alla fabbrica precedente, salvaguardata dall&rsquo;intervento palladiano e integrata nel nuovo complesso monumentale. La sala posta a sud-ovest del piano nobile venne decorata alla fine del Settecento. Il Palazzo costituisce un significativo esempio della capacit&agrave; di Palladio di ricomporre in forma monumentale edifici preesistenti, e una testimonianza della qualit&agrave; urbanistica dei suoi interventi, attestata nel caso specifico dall&rsquo;abilit&agrave; nel conferire nuova dignit&agrave; e rappresentativit&agrave; urbana all&rsquo;angolo fra contr&agrave; Porti e contr&agrave; Riale. La composizione architettonica del fronte principale traduce in un linguaggio pi&ugrave; carico di effetti pittorici, esito di una serrata integrazione tra architettura e apparato decorativo che contraddistingue la fase pi&ugrave; tarda dell&rsquo;attivit&agrave; palladiana, lo schema bramantesco con bugnato al piano terreno gi&agrave; adottato per il vicino Palazzo Iseppo Porto.', 'Palazzo Barbaran da Porto', 'images/vicenza/arte/barbaran.jpg', NULL, NULL, NULL, NULL, 'Facciata del palazzo Barbaran da Porto', 23),
('attrazioni', 'Suddivisi per ordine di difficolt&agrave;, chiunque pu&ograve; trovare il percorso pi&ugrave; adatto: dai piccoli esploratori agli adulti desiderosi di forti emozioni. Ma l&rsquo;avventura non finisce qui! Il parco &egrave; infatti inserito in un contesto naturale meraviglioso e tutto da scoprire in una vallata ricca di scorci incontaminati e selvaggi. Spiagge di bianca ghiaia vengono bagnate dalle limpide acque del torrente Centa, offrendo anche la vista della spettacolare cascata del Valimpach.<br>\r\nPer i gruppi Acropark organizza attivit&agrave; complementari ai percorsi acrobatici, sempre all&rsquo;insegna dell&rsquo;avventura. Diverse opzioni di camminata, accompagnati da guide esperte lungo le sponde del Rio Centa. L&rsquo;idea &egrave; nata all&rsquo;estero. In altri paesi come Francia e Germania, i percorsi acrobatici tra gli alberi sono gi&agrave; da tempo sviluppati e esistono molte installazioniche offrono questo servizio. In Italia, nonostante le qualit&agrave; boschive uniche del nostro paese, questo tipo di attivit&agrave; sta solo ora trovando uno sviluppo concreto, suscitando un interesse di pubblico sempre maggiore. Acropark vuole promuovere questo sport, consapevole del connubio tra attivit&agrave; fisica e mentale assieme alla sintonia e rispetto per la natura, in totale sicurezza. <br><bR>\r\nLE FONDAMENTA SONO GLI ALBERI<br>\r\nGli arredi sono delle piattaforme in legno, fissate ai tronchi ad una certa altezza, che comunicano tra loro attraverso collegamenti aerei di vario tipo. Funi di acciaio, liane, ponti tibetani, scale, reti ed altro, compongono percorsi di varia difficolt&agrave;, solitamente percorribili in 30 &ndash; 60 minuti. Altro elemento importantissimo &egrave; il DPI (Dispositivo Individuale di Protezione), un set di sicurezza simile a quello usato per affrontare le vie ferrate di montagna. Parte integrante del parco &egrave; il rifugio in legno con funzione di punto di accoglienza, deposito attrezzature e bar.<br>\r\nDivertirsi in un parco Acropark &egrave; molto semplice. Al punto di accoglienza viene fornita l&rsquo;attrezzatura necessaria (1), ci si sposta nell&rsquo;area Breefing dove si indossa l&rsquo;imbraco e vengono spiegate le regole da osservare durante il percorso (2 e 3). Eseguiti alcuni passaggi con l&rsquo;istruttore, si passa al percorso vero e proprio. Gli eventuali passaggi &ldquo;arditi&rdquo; sono sempre aggirabili con collegamenti pi&ugrave; facili per chi non si sentisse sufficientemente preparato.', 'Acropark di Roana(VI)', 'images/vicenza/attrazioni/acropark.jpg', NULL, NULL, NULL, NULL, 'Gruppo di persone intento ad affrontare uno dei percorsi sugli alberi che offre questo parco', 24),
('attrazioni', 'Adagiata tra il Massiccio del Grappa ad est e l&rsquo;Altopiano dei Sette Comuni ad ovest, la Valbrenta offre scenari di rara bellezza ed &egrave; una delle mete pi&ugrave; ambite per gli amanti degli sport fluviali di tutto il Veneto. Al centro della vallata c\'&egrave; il Comune di Valsatagna, una striscia abitata che si affaccia sul fiume Brenta. Il luogo ideale per andare in canoa, praticare il rafting ed imparare a confrontarsi con la rapidit&agrave; dei fiumi di montagna. Per tutte queste attivit&agrave; proprio a Vastagana c\'&egrave; la sede il Centro nazionale rafting e Canova sul fiume Brenta: &quot;Ivan Team&quot;. Il Centro fornisce una serie di servizi sia a prIvati che a scuole e istitutzioni. Ecco queli consigliati per chi &egrave; alle prime armi e vuole spendere un paio d\'ore sopra a un battelo o a una canoa.<br>\r\nRAFTING FACILE: Grazie a sicuri battelli pneumatici, salvagenti, caschetti ed esperte guide fluviali, si potranno ammirare le meraviglie del fiume Brenta da un insolito punto di vista. Le persone di tutte le et&agrave; potranno partecipare a queste escursioni in battello dove si imparer&agrave; a pagaiare, a conoscere il fiume e dove si percorreranno i luoghi pi&ugrave; suggestivi e ricchi di storia della nostra Valle. Dopo aver indossato l&rsquo;attrezzatura alle Grotte di Oliero, si inizier&agrave; la discesa (4 km) lungo il Brenta. Giunti all&rsquo;arrivo, con i mezzi messi a disposizione dal Centro, verrete ricondotti alle Grotte di Oliero. Nel battello possono salire circa una ventina di persone.<br> RAFTING AVVENTURA: Dieci km di fiume a bordo di una bolla d\'aria, sperimentando tutte le bizzarrie che l\'acqua inventa lungo il suo scorrere a valle. Il rafting &egrave; una successione infinita di emozioni mozzafiato da condividere con i tuoi compagni di pagaia lungo i salti e le rapide spumeggianti. E sul gommone ci sei tu, protagonista dell\'avventura dove &egrave; possibile fare i tuffi e lasciarsi trasportare dalla corrente e dalle emozioni. <br>\r\nRAFTING AVANZATO: In uno dei luoghi pi&ugrave; incantevoli del Veneto, percorrerete le acque bianche del fiume Brenta tra rapide spumeggianti, onde e salti, circondati da un ambiente isolato e suggestivo che vi lascer&agrave; incantati. Il fiume, a tratti calmo e a tratti impetuoso, la pace dei paesi allungati sulle rive, le lunghe file dei terrazzamenti, un tempo coltivati a tabacco, che scandiscono con i loro muri a secco i ripidi pendii a ridosso delle alte pareti dolomitiche a strapiombo fin sulle sponde del fiume.&ldquo;', 'Rafting e canoa sul Brenta a Valstagna!\r\n\r\n', 'images/vicenza/attrazioni/rafting.jpg', NULL, NULL, NULL, NULL, '8 persone con caschetto rosso sopra un gommone giallo nei pressi di una mini cascata nel loro percorso di rafting', 25),
('attrazioni', 'Lumignano &egrave; uno dei centri d\'arrampicata sportiva pi&ugrave; importanti del Nord Italia. Una falesia apprezzata per la bellezza dell\'arrampicata e la qualit&agrave; della roccia, ma anche per la difficolt&agrave; e il valore storico di molti itinerari. Infatti, proprio negli anni in cui la moderna arrampicata sportiva nasceva e si sviluppava, le striature di &quot;Lumignano classica&quot; hanno visto il massimo del loro fulgore e hanno attratto molti tra i pi&ugrave; forti climbers. La storia di queste pareti inizia gi&agrave; nel 1924 quando, sembra ad opera di Severino Casara, fu salito il diedro-fessura della &quot;Sbrega&quot;. Poi, negli anni \'70, arrivarono Renato Casarotto e ancora pi&ugrave; tardi Heinz Mariacher. Il grande lavoro di chiodatura &egrave; dovuto all\'opera del tutt\'ora molto attivo Michele Guerrini, senza dimenticare alcuni contributi d\'eccezione come quelli, tra gli altri, di Martin Scheel e Manolo. <br>\r\nACCESSO: Lumignano &egrave; situata pochi chilometri a sud della citt&agrave; di Vicenza. Da Vicenza si imbocca la statale 247 per Este che costeggia tutti i colli Berici. Si giunge al paese di Longare superato il quale si devia a destra per Costozza e Lumignano. Dopo il primo dei due paesi e dopo alcune curve si raggiungono le rocce che sovrastano il paese. Per salire alle pareti pi&ugrave; classiche si supera la chiesa e si continua per la strada fino al suo termine. Si parcheggia in prossimit&agrave; di un grande masso. <br>\r\nARRAMPICATA: L&rsquo;arrampicata &egrave; tecnica ed impegnativa, e in genere le vie sono esplosive e dalle gradazioni severe. Nella zona strapiombante l\'arrampicata &egrave; atletica, su buchi spesso dolorosi, mentre sulle pareti verticali l&rsquo;arrampicata &egrave; molto tecnica e richiede un ottimo uso dei piedi', 'Arrampicata a Lumignano ', 'images/vicenza/attrazioni/lumignano.jpg', NULL, NULL, NULL, NULL, 'Montagna con distese erbose sulla cima.', 26),
('attrazioni', '', '', '', NULL, NULL, NULL, NULL, '', 27);

